import React from 'react';
import VideoItem from '../video-item/video-item.component';

import { Player, ControlBar,
	  ReplayControl,
	  ForwardControl,
	  CurrentTimeDisplay,
	  TimeDivider,
	  PlaybackRateMenuButton,
	  VolumeMenuButton} from 'video-react';

import './video-preview.styles.scss';

const VideoPreview = ({title, items, Player }) => (
	<div className='video-preview'>
	  <h1 className='title'> { title.toUpperCase()} </h1>
		<div className='preview'>
			<Player>
			{
				items
				.filter((item, idx) => idx < 4)
				.map(({id, ...otherItemProps}) => (
				 <VideoItem key={id} {...otherItemProps} />
			))
			}
			</Player>
		</div>
	</div>
);

export default VideoPreview;
